mapper.select:
SELECT A.JOB_EXECUTION_ID ,A.VERSION ,A.JOB_INSTANCE_ID ,C.JOB_NAME AS JOB_NM /*,F.BAT_JOB_NM ,E.PRCT_TP ,E.FCAST_TRX_CNT ,E.DATASRC_TP ,E.JCHRGPSN_KEY ,E.JCHRGPSN_CNTCTPT ,E.COMMIT_CNT */ ,D.HOST_ID ,D.APSVR_ID ,D.BAT_JOB_RUN_KEY ,D.HOST_IP_ADDR ,A.CREATE_TIME ,A.START_TIME ,A.END_TIME ,A.STATUS ,A.EXIT_CODE ,A.EXIT_MESSAGE ,A.LAST_UPDATED ,B.SHORT_CONTEXT ,B.SERIALIZED_CONTEXT /*,J.SYS_NM ,I.PROJ_NM*/ ,COUNT(*) OVER () ROWCNT FROM fw.TB_BAT_JOB_EXECUTION A JOIN fw.TB_BAT_JOB_INSTANCE C ON A.JOB_INSTANCE_ID = C.JOB_INSTANCE_ID
AND UPPER(REPLACE(C.JOB_NAME, ' ', '')) LIKE CONCAT('%' , UPPER(REPLACE(#{JOB_NM , javaType=String, jdbcType=VARCHAR}, ' ', '')) , '%')
JOIN fw.TB_BAT_JOB_EXECUTION_CONTEXT B ON A.JOB_EXECUTION_ID = B.JOB_EXECUTION_ID JOIN fw.tb_bat_apsvr_BATPRCT D ON A.JOB_EXECUTION_ID = D.JOB_EXECUTION_ID /*JOIN fw.TB_BAT_JOB_RUN E ON D.BAT_JOB_RUN_KEY = E.BAT_JOB_RUN_KEY*/
/*AND E.PRCT_TP = #{PRCT_TP , javaType=String, jdbcType=VARCHAR}*/
/*AND E.BAT_JOB_RUN_KEY = #{BAT_JOB_RUN_KEY ,javaType=String, jdbcType=VARCHAR}*/
/*JOIN fw.TB_BAT_JOB F ON E.RSC_KEY = F.RSC_KEY*/
/* AND UPPER(REPLACE(F.BAT_JOB_NM, ' ', '')) LIKE CONCAT('%' , UPPER(REPLACE(#{BAT_JOB_NM ,javaType=String, jdbcType=VARCHAR}, ' ', '')) , '%')*/
/*JOIN fw.TB_RSC G ON F.RSC_KEY = G.RSC_KEY JOIN fw.ap_ide_PROJ H ON G.PROJ_KEY = H.PROJ_KEY JOIN fw.ap_ide_JOB_PROJ I ON H.JOB_PROJ_KEY = I.JOB_PROJ_KEY */
/* AND I.JOB_PROJ_KEY = #{JOB_PROJ_KEY}*/
/*JOIN fw.ap_ide_SYS J ON I.SYS_KEY = J.SYS_KEY*/
/* AND J.SYS_KEY = #{SYS_KEY}*/
WHERE 1 = 1
AND DATE_FORMAT(A.START_TIME, '%Y-%m-%d %H:%i:%s') >= CONCAT(#{START_DATE ,javaType=String, jdbcType=TIMESTAMP} , ' 00:00:00')
AND DATE_FORMAT(A.START_TIME, '%Y-%m-%d %H:%i:%s') <= CONCAT(#{END_DATE ,javaType=String, jdbcType=TIMESTAMP} , ' 23:59:59')
AND A.JOB_EXECUTION_ID = #{JOB_EXECUTION_ID ,javaType=String, jdbcType=VARCHAR}
AND E.JCHRGPSN_KEY = #{JCHRGPSN_KEY}
/* ORDER BY a.JOB_EXECUTION_ID */ ORDER BY a.JOB_EXECUTION_ID DESC
